import * as React from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import TabBody from "./TabBody";
import { useEffect } from "react";
import { useState } from "react";
import FormControl from '@mui/material/FormControl';


function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}


TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}


export default function TabComponent() {
  const [value, setValue] = useState(0);
  const [TotalCount, SetTotalCount] = useState([]);
  const [DDAssetsCount, SetDDAssetsCount] = useState([]);
  const [VendorAssetsCount, SetVendorAssetsCount] = useState([]);
  const [age, setAge] = React.useState('');


  const handleChange = (event, newValue) => {
    setValue(newValue);
  };


  const tabArray1 = {
    title: ["Total Assets Dashboard"],
    cardTitle: [
      `Total Asset`,
      `Replacement`,
      `In Stock`,
      `Deploy on FLoor`,
      `Damage `,
      `Return Back To Vendor `,
    ],
    counts: [Object.values(TotalCount)],
  };
  const tabArray2 = {
    title: ["DD Site", "OMR Site", "Mysore Site", "Belgaum Site"],
    cardTitle: [
      `Total Asset`,
      `Replacement`,
      `In Stock`,
      `DeployFLoor`,
      `Damage`,
      `Return Back To Vendor`,
    ],
  counts: DDAssetsCount.map((item) => {
      let a = Object.values(item);
      a.shift();
      return a;
    }),
  };


  const tabArray3 = {
    title: [
      "Winger IT Solutions",
      "NXTGEN IT Solutions",
      "Sheeltron Digital Systems Pvt.Ltd.",
    ],
    cardTitle: [
       `Total Asset`, 
       `Desktop`, 
       `Laptop`,
  ],


    counts: VendorAssetsCount.map((item) => {
      let a = Object.values(item);
      a.shift();
      return a;

    }),
  };

  
  const ApiDataCall = () => {
    fetch("/api/TotalAssets")
      .then((res) => res.json())
      .then((data) => {
        if (data.total > 0) {
          SetTotalCount(data.items[0]);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const ApiDataCall1 = () => {
    // calling api here ..
    fetch("/api/DDAssets")
      .then((res) => res.json())
      .then((data) => {
        if (data.total > 0) {
          SetDDAssetsCount(data.items);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };

  const ApiDataCall2 = () => {
    fetch("/api/VendorAssets")
      .then((res) => res.json())
      .then((data) => {
        if (data.total > 0) {
          SetVendorAssetsCount(data.items);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  };

  useEffect(() => {
    ApiDataCall();
    ApiDataCall1();
    ApiDataCall2();
  }, []);

  return (
    <Box sx={{ width: "100%" }}>
      <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
        >
          <Tab label="Total Assets dashboard" {...a11yProps(0)} />
          <Tab label="Site Wise Assets Count" {...a11yProps(1)} />
          <Tab label="Vendor Wise Asset Count" {...a11yProps(2)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
        <TabBody CardTitleArray={tabArray1} />
      </TabPanel>
      <TabPanel value={value} index={1}>
        <TabBody CardTitleArray={tabArray2} />
      </TabPanel>
      <TabPanel value={value} index={2}>
        <TabBody CardTitleArray={tabArray3} />
      </TabPanel>
    </Box>
  );

  <div>
  <FormControl sx={{ m: 1, minWidth: 120 }}>
    <InputLabel id="demo-simple-select-helper-label">Age</InputLabel>
    <Select
      labelId="demo-simple-select-helper-label"
      id="demo-simple-select-helper"
      value={age}
      label="Age"
      onChange={handleChange}
    >
      <MenuItem value="">
        <em>None</em>
      </MenuItem>
      <MenuItem value={10}>Ten</MenuItem>
      <MenuItem value={20}>Twenty</MenuItem>
      <MenuItem value={30}>Thirty</MenuItem>
    </Select>
    <FormHelperText>With label + helper text</FormHelperText>
  </FormControl>
  <FormControl sx={{ m: 1, minWidth: 120 }}>
    <Select
      value={age}
      onChange={handleChange}
      displayEmpty
      inputProps={{ 'aria-label': 'Without label' }}
    >
      <MenuItem value="">
        <em>None</em>
      </MenuItem>
      <MenuItem value={10}>Ten</MenuItem>
      <MenuItem value={20}>Twenty</MenuItem>
      <MenuItem value={30}>Thirty</MenuItem>
    </Select>
    <FormHelperText>Without label</FormHelperText>
  </FormControl>
</div>
}
